def valid_paths(n, p, c):
    if n == 0 or p == 0 or c == 0 or len(c) == 0:
        return 0

    # Initialize DP and dp[0]
    dp = [[-1 for _ in range(n)] for _ in range(n + 1)]
    dp[0][0] = n
    dp[0][1] = p
    dp[0][0] = 0

    # dp[n][p]
    for i in range(1, n):
        for j in range(1, p):
            # dp[i][j] = dp[i][j-1] + dp[i-1][j-1]
            dp[i][j] = dp[i][j - 1] + dp[i - 1][j]

    return dp[n][p]

