def find_frame(n, m, screen):
    for i in range(n):
        for j in range(m):
            if screen[i][j] == 'w':
                frame = flood_fill(n, m, i, j, screen, [])
                if frame is not None:
                    return frame
    return [-1]

def flood_fill(n, m, i, j, screen, frame):
    if i < 0 or i >= n or j < 0 or j >= m:
        return None
    if screen[i][j] == '.':
        return None
    screen[i][j] = '+'
    frame.append((i, j))
    flood_fill(n, m, i + 1, j, screen, frame)
