def number_of_sequences(n, T, songs):
    # Create a dictionary to store the count of each genre
    genres = {}
    for song in songs:
        if song[1] not in genres:
        genres[song[1]] += 1

    # Initialize the dp array
    dp = [[[0 for _ in range(len(genres) + 1)] for _ in range(T + 1)] for _ in range(n + 1)]

    # Initialize the base cases
    for genre in genres:
        dp[0][0][genre] = dp[0][1] + dp[1][0][genre]

    # Populate the dp array
    for i in range(1, n + 1):
        for j in range(T + 1):
            for genre in genres:
                for j in range(i + 1, T + 1):
                    if dp[i][j][genre] > dp[i][j - 1][genre]:
                        dp[i][j][genre] = dp[i][j - 1][genre]
