def kth_smallest_divisor(n, k):
    divisors = []
    while n > 0:
        i = n // k
        if i not in divisors:
            divisors.append(i)
            n //= k
        else:
            if k == len(divisors) - 1:
            divisors.append(i)
            return i
    return -1


def kth_greatest_divisor(n, k):
    divisors = []
    while n > 0:
        i = n % k
        if i not in divisors:
            divisors.append(i)
            n //= k
        else:
            if k == len(divisors) - 1:
                divisors.append(i)
                return i
    return -1


