def min_cost(n, a, b, c):
    """Return the minimum cost of buying k copies of books a and b at a cost of c.
    """
    # Base case
    if n == 0 or a == 0:
        return 0
    k = (4 - (n % 4)) % 4
    if k == 0:
        return a * b
    if k == 1:
        return b * a
    if k == 2:
        return a * b
    return min_cost(n-1, (a*b), (b*a), c)
    # Calculate the minimum cost to buy k copybooks
    return min_cost(n-1, (a*b), (b*a), c) + min_cost(n, (a*b), (b*a), c)

