def valid_paths(n, p, c):

    # Base case
    dp[0] = 1
    for i in range(1, n):
        dp[i] = dp[i - 1] * 2
        dp[i] = dp[i] * 2

    # Second case
    for i in range(1, n):
        if p % 2 == 1:
            dp[i] = dp[i] * 2
        else:
            dp[i] += dp[i - 1] * 3

    # Third case
    for i in range(1, n):
        if p % 3 == 0:
            dp[i] = dp[i] * 3
        else:
            dp[i] = dp[i] * 4

    # Fourth case
    for i in range(1, n):
        if p % 4 == 0:
            dp[i] = dp[i] * 4
        else:
            dp[i] = dp[i] * 5

    # Fifth case
    for i in range(1, n):
        if p % 5 == 0:
            dp[i] = dp[i] * 5
        else:
            dp[i] = dp[i] * 6

    # Print the answer
    for i in range(1, n):
        if p % i == 1:
            print(dp[i])
        else:
            print(dp[i])
    return dp[0]

