def get_special_numbers(n, k):
    dp = [0] * (n + 1)
    dp[1] = 1
    for i in range(2, n + 1):
        if i & 1:
            dp[i] = dp[i - 1]
        else:
            dp[i] = dp[i // 2]
        for j in range(1, i):
            if get_set_bits(j) == k:
                dp[i] = (dp[i] + dp[i - j]) % mod
    return dp[n]

