from copy import deepcopy

class BitSet:
    def __init__(self, n):
        self.size = n
        self.dp = [0 for i in range(n + 1)]
        for i in range(n+1):
            self.dp[i] = 0

    def __contains__(self, key):
        return key in self.dp

    def add(self, key):
        if key & 1:
            self.dp[key] += 1
        else:
            self.dp[key] = 0

    def count(self, key):
        return key & 1

    def count_set(self, key):
        return key & 1

    def union(self, other):
        self.dp[0] += self.dp[1]
        for key in other.dp:
            self.dp[key] += other.dp[key]

    def difference(self, other):
        self.dp[0] += self.dp[1]
        for key in other.dp:
            self.dp[key] -= other.dp[key]

    def intersection(self, other):
        self.dp[0] += self.dp[1]
        for key in other.dp:
            self.dp[key] -= other.dp[key]

    def difference_in(self, other):
        self.dp[0] += self.dp[1]
        for key in other.dp:
