def all_pairs(V):
    seen = set()
    pairs = set()

    for u in V:
        u = u - 1
        for v in V:
            if u + 1 != v:
                if u in seen or v in seen:
                    continue

                seen.add(u)
                seen.add(v)
                pairs.add((u,v))
                pairs.add((v,u))

    return pairs

