        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def solve(n, m):
    total_seqs = 2**(m * n)
    ans = total_seqs
    for i in range(2, m + 1):
        if is_prime(i):
            ans -= total_seqs // i
    for i in range(2, m + 1):
        for j in range(i + 1, m + 1):
