class ListNode:
    def __init__(self, x):
        self.val = x
        self.next = None

class Solution:
    def findMax(self, head: ListNode) -> int:
        ans = 0
        n = head
        while n.next is not None:
            n = n.next
            ans = max(ans, n.val)
        return ans
