import sys

def get_number_of_pairs(n, m, p):
    """
    Returns the number of pairs of elements in n and m such that
    no element of m is greater than any element of n and no element of m is less than any element of n.
    """
    for i in range(n):
        # Find the position of m in the subarray p[i:]
        j = search_right(p[i:], m)
        if j < len(p[i:]):
            # If m is found, then count the number of elements to the right of m
            count += len(p[i:]) - j - 1
        else:
            # If m is not found, then m is greater than all elements in the subarray
            count = 1
            break
    return count

