inv_fact = [1, 1]

def precompute(n):
    for i in range(2, n + 1):
        fact.append((fact[-1] * i) % MOD)
        inv.append((MOD - (MOD // i) * inv[MOD % i] % MOD) % MOD)
        inv_fact.append((inv_fact[-1] * inv[i]) % MOD)

def C(n, k):
    if k > n or k < 0:
        return 0
    return fact[n] * inv_fact[k] * inv_fact[n - k] % MOD

def solve(f, w, h):
    precompute(f + w)
    if f < w:
        f, w = w, f
    ans = 0
    for i in range(w + 1):
        if i < h:
            continue
        cur = C(f + i - 1, i) * fact[i] % MOD * fact[f] % MOD
        if i != w:
            cur = cur * (w - i + 1) % MOD
        ans = (ans + cur) % MOD
    return ans

