def flood_fill(n, m, i, j, screen, frame):
    if i < 0 or i >= n or j < 0 or j >= m:
        return None
    if screen[i][j] == '.':
        return None
    screen[i][j] = '+'
    frame.append((i, j))
    flood_fill(n, m, i + 1, j, screen, frame)
    flood_fill(n, m, i - 1, j, screen, frame)
    flood_fill(n, m, i, j + 1, screen, frame)
    flood_fill(n, m, i, j - 1, screen, frame)
    w, h = 1, 1
    for x, y in frame:
        w = max(w, x + 1 - x)
        h = max(h, y + 1 - y)
    if w == h:
        return screen
    return None

