import math

def factors(n):
    result = set()
    for i in range(1, int(n ** 0.5) + 1):
        div, mod = divmod(n, i)
        if mod == 0:
            result |= {i, div}
    return result

def order(x, m):
    for i in factors(m):
        if pow(x, i, m) == 1:
            return i
    return m

