import math

def compress(n, I, A):
    K = len(set(A))
    k = math.ceil(math.log2(K))
    if k * n / 8 <= I:
        return 0
    res = float('inf')
    for l in range(K):
        for r in range(l + 1, K + 1):
            cnt = 0
            for a in A:
                if a < l or a > r:
                    cnt += 1
            if cnt < res and k * n / 8 <= I - cnt:
                res = cnt
    return res

