import math

def pow_mod(x, y, p):
    res = 1
    while y > 0:
        if y & 1:
            res = (res * x) % p
        y = y >> 1
        x = (x * x) % p
    return res

def fact(n, p):
    res = 1
    for i in range(1, n + 1):
        res = (res * i) % p
    return res

def inv(x, p):
    return pow_mod(x, p - 2, p)

def nCr(n, r, p):
    if r > n - r:
        r = n - r
    if r == 0:
        return 1
    if r == 1:
        return n
    num = den = 1
    for i in range(r):
        num = (num * (n - i)) % p
        den = (den * (i + 1)) % p
    return (num * inv(den, p)) % p

