def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def answer(N, K, A):
    g = A[0]
    for i in range(1, N):
        g = gcd(g, A[i])
    for i in range(N):
        A[i] //= g
    for i in range(K):
        mx = 0
        for j in range(N):
            mx = max(mx, A[j])
        A[mx] -= 1
        g = gcd(g, mx + 1)
    return g

