def min_watering(n, h):
    h = [0] + h
    ans = 0
    l, r = 0, 0
    for i in range(1, n + 1):
        if h[i] > h[r]:
            r = i
        if i == n or h[i] <= h[i + 1]:
            ans += max(0, h[r] - h[l])
            h[l:r + 1] = [h[r]] * (r - l + 1)
            l = i
            r = i
    return ans


