def climb_stairs(N, M, a):
    dp = [0] * (N + 1)
    dp[0] = 1
    for i in range(1, N + 1):
        if i - 1 in a:
            dp[i] = dp[i - 2]
        elif i - 2 in a:
            dp[i] = dp[i - 1]
        else:
            dp[i] = (dp[i - 1] + dp[i - 2]) % 1000000007
    return dp[N]


