def getMaxDivisibleBy3(s):
    n = len(s)
    dp = [[0, 0] for _ in range(n + 1)]

    for i in range(1, n + 1):
        dp[i][0] = dp[i - 1][0]
        dp[i][1] = dp[i - 1][1] + int(s[i - 1])

        if dp[i][1] >= 3:
            dp[i][1] %= 3

    ans = 0

    for i in range(1, n + 1):
        if dp[i][0] == dp[i][1]:
            ans += 1

    return ans


