import math

def min_time(n, p, q, a, b):
    dp = [[math.inf] * (p + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    for i in range(1, n + 1):
        for j in range(p + 1):
            for k in range(min(j // a[i - 1], q // b[i - 1]) + 1):
                dp[i][j] = min(dp[i][j], dp[i - 1][max(0, j - a[i - 1] * k)] + k)
    return dp[n][p] / q

