    n = len(bin(int(x, 2))) - 2
    count = 0
    for i in range(2**n):
        diff = i ^ int(x, 2)
        temp = min(i, diff)
        count = (count + power(2, temp & (n - 1), 1000000007)) % 1000000007
    return count

