        elif stack and stack[-1] == '(':
            stack.pop()
        else:
            stack.append(c)
    return not stack

def solve(n, s):
    ans = 0
    for i in range(n):
        if s[i] == '(':
            s_new = s[:i] + ')' + s[i+1:]
        else:
            s_new = s[:i] + '(' + s[i+1:]
        if is_regular(s_new):
            ans += 1
    return ans

