def generate_brainfuck(expression):
    def char_to_bf(c):
        if c == '>':
            return '>'
        elif c == '<':
            return '<'
        elif c == '+':
            return '+'
        elif c == '-':
            return '-'
        elif c == '[':
            return '['
        elif c == ']':
            return ']'

    def eval_expr(expr):
        stack = []
        for token in expr.split():
            if token.isdigit():
                stack.append(int(token))
            elif token in ('+', '-'):
                b = stack.pop()
                a = stack[-1]
                if token == '+':
                    stack[-1] = a + b
                else:
                    stack[-1] = a - b
        return stack[0]

    expr_bytes = [eval_expr(expression)]
    bf_program = ''.join(char_to_bf(c) for c in expr_bytes[0])
    bf_program += '>.<[->+<]>.'

    return bf_program


