# bfs for "ssph"
def bfs(s):

    i = 0
    frontier = [s]
    while frontier:

        if mark[s]:
            break;

        next_frontier = []
        for u in frontier:

            # check next state
            for v, isState in enumerate(edges):
                if isState:
                    # check new node
                    state = u + (n - 1000) - v

                    if state >= 0 and state <= 2000 and not mark[state]:
                        mark[state] = True
                        next_frontier.append(state)

        frontier = next_frontier
        i += 1

    if mark[s]:
        return i
    else:
        return -1

