    dp = [0] * (n + 1)
    dp[0] = 1
    cnt = Counter(boxes)
    for i in range(n):
        for j in range(i + 1, n):
            if gcd(boxes[i], boxes[j]) == boxes[i]:
                dp[j] += dp[i]
    return max(dp)

