import sys

def mex(x, y):
    if x == 0 and y == 0:
        return 1
    elif x == 0 and y == 1:
        return 2
    elif x == 1 and y == 0:
        return 2
    elif x == 1 and y == 1:
        return 0
    elif x == 2 and y == 0:
        return 1
    elif x == 2 and y == 1:
        return 0

def solve(N, input_matrix):
    matrix = [[0 for _ in range(N)] for _ in range(N)]
    matrix[0] = input_matrix[0]
    for i in range(1, N):
        matrix[i][0] = input_matrix[i][0]

    for i in range(1, N):
        for j in range(1, N):
            matrix[i][j] = mex(matrix[i-1][j], matrix[i][j-1])

    count_0 = 0
    count_1 = 0
    count_2 = 0
    for row in matrix:
        for element in row:
            if element == 0:
                count_0 += 1
            elif element == 1:
                count_1 += 1
            elif element == 2:
                count_2 += 1

    return (count_0, count_1, count_2)

if __name__ == '__main__':
    N = int(input())
    input_matrix = []
    for _ in range(N):
        input_matrix.append([int(x) for x in input().split()])
    result = solve(N, input_matrix)
    print(*result)

def gcd(a, b):
    while b != 0:
        a, b = b, a % b
    return a

def get_polynomials(n):
    if n < 1:
        return -1
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            for k in range(1, n + 1):
                A = [1] + [0] * (i - 1) + [k]
                B = [1] + [0] * (j - 1)
                steps = 0
                while True:
                    steps += 1
                    if steps > n:
                        break
                    R = [x % 2 for x in A]
                    while R and R[-1] == 0:
                        R.pop()
                    if not R:
                        break
                    A = B
                    B = R
                if steps == n:
                    return A, B
    return -1

def main():
    n = int(input())
    result = get_polynomials(n)
    if result == -1:
        print(-1)
    else:
        A, B = result
        print(len(A) - 1)
        print(*A)
        print(len(B) - 1)
        print(*B)

if __name__ == '__main__':
    main()

def max_team_size(l, r, a):
    team_size = 0
    if l + r > a:
        team_size = 2 * min(l, r) + a
    elif l + r <= a and l + r > 0:
        team_size = l + r
    return team_size

