from math import sqrt

def is_square(n):
    root = int(sqrt(n))
    return root * root == n

def find_triple(n):
    if n % 2 == 0:
        m = (n // 2) ** 2 - 1
        if is_square(m):
            return int(sqrt(m)), n // 2
    else:
        m = (n ** 2 - 1) // 2
        if is_square(m):
            return int(sqrt(m)), (n ** 2 - 1) // 2
    return -1, -1

