                    return False

    for i in range(m):
        if ver_dir[i] == '^':
            for j in range(1, n - 1):
                if hor_dir[j] == '>':
                    return False
        if ver_dir[i] == 'v':
            for j in range(1, n - 1):
                if hor_dir[j] == '<':
