def get_wall_x(y_1, y_2, y_w, x_b, y_b, r):
    if y_w >= y_1 and y_w <= y_2:
        return -1  # The wall is inside the goal
    if y_w < y_b:
        return -1  # The ball is behind the wall

    x_w = x_b + (r * (y_w - y_b)) / (y_w - y_1)
    return x_w


