def rock_paper_scissors(n, a1, a2, a3, b1, b2, b3):
    # Find the minimum number of rounds Alice can win
    min_win = 0
    for i in range(a1):
        for j in range(b2):
            min_win += 1
    for i in range(a2):
        for j in range(b3):
            min_win += 1
    for i in range(a3):
        for j in range(b1):
            min_win += 1

    # Find the maximum number of rounds Alice can win
    max_win = 0
    for i in range(a1):
        for j in range(b1):
            max_win += 1
    for i in range(a2):
        for j in range(b2):
            max_win += 1
    for i in range(a3):
        for j in range(b3):
            max_win += 1

    return min_win, max_win

