def base_2_representation(N):
    if N == 0:
        return "0"
    result = ""
    is_negative = N < 0
    if is_negative:
        N = -N
    while N != 0:
        rem = N % (-2)
        if rem < 0:
            rem += 2
            N += 1
        result += str(rem)
        N //= -2
    if is_negative:
        result = "-" + result[1:]
    return result[::-1]

