def philospher_stone(a):
    operations = [aqua_fortis, aqua_regia, amalgama, minium, vitriol]
    candidates = apply_operations(1, operations)
    for num in a:
        candidates = apply_operations(num, operations)
    for i in range(2, max(candidates) + 1):
