def valid(board, x, y):
    # Check horizontal
    for i in range(10):
        if board[x][i] == board[x][y] and board[x][i] != '.':
            return True

    # Check vertical
    for j in range(10):
        if board[j][y] == board[x][y] and board[j][y] != '.':
            return True

    # Check diagonal
    dx, dy = -1, 1
    for i in range(x, 10, dx):
        for j in range(y, 10, dy):
            if board[i][j] == board[x][y] and board[i][j] != '.':
                return True

    # No winning line found
