	if s == 1:
		# 1 - a/b
		a = -a + b
	elif s == 2:
		# 2 + a/b - 1 = a/b + 1
		a = a + b
	elif s == 3:
		# 3 - a/b
		a = -a + 3*b
	else:
		# 4 + a/b - 1 = a/b + 3
		a = a + 3*b
	return a/b

def vec(n, x, y):
	# cos = x/sqrt(xx+yy)
	a = x*x
	b = l = x*x + y*y
	s = sec(x, y)
	v = val(a, b, s)
	return (v, n, x, y, l)

