from math import sqrt, floor
from collections import Counter

def factorize(n):
    limit = floor(sqrt(n))
    factor = Counter()

    p = 2
    while n % p == 0:
        factor[p] += 1
        n //= p

    for p in range(3, limit + 1, 2):
        while n % p == 0:
            factor[p] += 1
            n //= p
    if n > 1:
        factor[n] += 1

    return factor

def make_divisors(n):
    result = [1]

    for p, e in factorize(n).items():
        result = [x * p**i for i in range(e + 1) for x in result]

    return sorted(result)

