def get_smallest_beautiful_number(n, k, x):
    if n <= k:
        return x

    i = 0
    while i < n - k:
        if x[i] < x[i + k]:
            return ''.join(x[:i + 1]) + ''.join(x[i + 1:])
        i += 1

    return ''.join(x)


