    for i in range(len(packmans)):
        x = stars[first_to_eat]
        if packmans[i] > x:
            if packmans[i] - x > t:
                return False
            d1 = t - 2 * (packmans[i] - x)
            d2 = (t - (packmans[i] - x)) // 2
            first_to_eat = bisect.bisect_right(stars, packmans[i] + max(d1, d2))
