            if first_to_eat < len(stars) and stars[first_to_eat] == packmans[i] + t:
                first_to_eat += 1
            first_to_eat = max(j, first_to_eat)
        if first_to_eat >= len(stars):
            return True
    return first_to_eat >= len(stars)


