def binary_search(num_max):
    if num_max <= 0:
        return 0

    left = 0
    right = num_max
    while left <= right:
        mid = (left + right) // 2
        mid_value = calc_value(mid)
        if mid_value == X:
            return mid
        elif mid_value < X:
            left = mid + 1
        else:
            right = mid - 1

