def resolve():
    S = input()

    l = len(S)
    ans = "AC"
    cf = False
    for i in range(l):
        if i == 0:
            if S[i] != "A":
                ans = "WA"
                break
        elif i == 1 or i == l-1:
            if S[i] == "C" or S[i].isupper():
                ans = "WA"
                break
        else:
            if S[i] == "C":
                if cf == True:
                    ans = "WA"
                    break
                else:
                    cf = True
            elif S[i].isupper():
                ans = "WA"
                break
    else:
        if cf == False:
            ans = "WA"

    print(ans)


